import { useState } from "react";
import { getContract } from "./contractUtils"; // import the helper

function App() {
  const [account, setAccount] = useState(null);
  const [name, setName] = useState("");
  const [points, setPoints] = useState("");

  const connectWallet = async () => {
    if (window.ethereum) {
      const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
      setAccount(accounts[0]);
    } else {
      alert("Please install MetaMask");
    }
  };

  const handleAddReward = async () => {
    try {
      const contract = await getContract();
      const tx = await contract.addReward(name, parseInt(points));
      await tx.wait();
      alert("Reward added successfully to blockchain!");
    } catch (err) {
      console.error("Error adding reward:", err);
    }
  };

  return (
    <div>
      <button onClick={connectWallet}>Connect Wallet</button>
      {account && <p>Connected: {account}</p>}

      <div>
        <input placeholder="Reward Name" value={name} onChange={(e) => setName(e.target.value)} />
        <input placeholder="Points" value={points} onChange={(e) => setPoints(e.target.value)} />
        <button onClick={handleAddReward}>Add Reward</button>
      </div>
    </div>
  );
}

export default App;
