import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import { getReadOnlyContract, getSignerContract } from './contractUtils';

function App() {
  const [walletAddress, setWalletAddress] = useState('');
  const [rewardName, setRewardName] = useState('');
  const [rewardPoints, setRewardPoints] = useState('');
  const [rewards, setRewards] = useState([]);
  const [contract, setContract] = useState(null);

  // Load rewards from read-only contract on load
  useEffect(() => {
    const loadRewards = async () => {
      const readOnlyContract = getReadOnlyContract();
      setContract(readOnlyContract);
      try {
        const count = await readOnlyContract.getRewardCount();
        const rewardsArray = [];
        for (let i = 0; i < count; i++) {
          const reward = await readOnlyContract.getReward(i);
          rewardsArray.push({ name: reward[1], points: reward[2].toString() });
        }
        setRewards(rewardsArray);
      } catch (error) {
        console.error('Error loading rewards:', error);
      }
    };
    loadRewards();
  }, []);

  // Connect wallet only when user wants to add reward
  const connectWallet = async () => {
    if (!window.ethereum) {
      alert('MetaMask not detected');
      return null;
    }
    try {
      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      setWalletAddress(accounts[0]);
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      const signerContract = getSignerContract(signer);
      setContract(signerContract);
      return signerContract;
    } catch (err) {
      console.error('Wallet connection failed:', err);
      return null;
    }
  };

  const handleAddReward = async () => {
    if (!walletAddress) {
      const signerContract = await connectWallet();
      if (!signerContract) return; // stop if no wallet
    }

    try {
      const tx = await contract.addReward(rewardName, parseInt(rewardPoints));
      await tx.wait();

      // After success, reload rewards with read-only contract (safer)
      const readOnlyContract = getReadOnlyContract();
      const count = await readOnlyContract.getRewardCount();
      const rewardsArray = [];
      for (let i = 0; i < count; i++) {
        const reward = await readOnlyContract.getReward(i);
        rewardsArray.push({ name: reward[1], points: reward[2].toString() });
      }
      setRewards(rewardsArray);

      setRewardName('');
      setRewardPoints('');
    } catch (err) {
      console.error('Error adding reward:', err);
    }
  };

  return (
    <div style={{ padding: '30px' }}>
      <h1>üéÅ Blockchain Reward System</h1>

      {walletAddress ? (
        <p>‚úÖ Connected: <b>{walletAddress}</b></p>
      ) : (
        <p>‚ö†Ô∏è Wallet not connected. Connect only when adding rewards.</p>
      )}

      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Reward Name"
          value={rewardName}
          onChange={(e) => setRewardName(e.target.value)}
        />
        <input
          type="number"
          placeholder="Points"
          value={rewardPoints}
          onChange={(e) => setRewardPoints(e.target.value)}
        />
        <button onClick={handleAddReward}>Add Reward</button>
      </div>

      <h3>üìã Rewards List:</h3>
      <ul>
        {rewards.length > 0 ? (
          rewards.map((r, i) => <li key={i}>{r.name} ‚Äî {r.points} points</li>)
        ) : (
          <li>No rewards found</li>
        )}
      </ul>
    </div>
  );
}

export default App;
