import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [name, setName] = useState('');
  const [points, setPoints] = useState('');
  const [rewards, setRewards] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/rewards')
      .then(res => setRewards(res.data))
      .catch(err => console.error(err));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !points) return;

    await axios.post('http://localhost:5000/rewards', { name, points });
    const res = await axios.get('http://localhost:5000/rewards');
    setRewards(res.data);
    setName('');
    setPoints('');
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Blockchain Reward System</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={e => setName(e.target.value)}
        />
        <input
          type="number"
          placeholder="Points"
          value={points}
          onChange={e => setPoints(e.target.value)}
        />
        <button type="submit">Submit</button>
      </form>

      <h2>Reward List</h2>
      <ul>
        {rewards.map((reward, index) => (
          <li key={index}>{reward.name} - {reward.points} points</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
