import React, { useState, useEffect } from "react";
import { getContract } from "./contractUtils";

function App() {
  const [account, setAccount] = useState(null);
  const [name, setName] = useState("");
  const [points, setPoints] = useState("");

  useEffect(() => {
    if (window.ethereum) {
      window.ethereum.on("accountsChanged", (accounts) => {
        if (accounts.length > 0) {
          setAccount(accounts[0]);
        } else {
          setAccount(null);
        }
      });
    }
  }, []);

  const connectWallet = async () => {
    if (window.ethereum) {
      try {
        const accounts = await window.ethereum.request({
          method: "eth_requestAccounts",
        });
        setAccount(accounts[0]);
      } catch (err) {
        console.error("Wallet connection failed:", err);
      }
    } else {
      alert("Please install MetaMask!");
    }
  };

  const handleAddReward = async () => {
    if (!name || !points) {
      alert("Please enter both name and points");
      return;
    }

    try {
      const contract = await getContract();
      const tx = await contract.addReward(name, parseInt(points));
      await tx.wait();
      alert("Reward added successfully!");
      setName("");
      setPoints("");
    } catch (err) {
      console.error("Error adding reward:", err);
      alert("Failed to add reward");
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h2>Blockchain Reward System</h2>
      <button onClick={connectWallet}>
        {account ? "Connected" : "Connect Wallet"}
      </button>
      {account && <p>Connected Wallet: {account}</p>}

      <div style={{ marginTop: "20px" }}>
        <input
          placeholder="Reward Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={{ marginRight: "10px" }}
        />
        <input
          type="number"
          placeholder="Points"
          value={points}
          onChange={(e) => setPoints(e.target.value)}
          style={{ marginRight: "10px" }}
        />
        <button onClick={handleAddReward}>Add Reward</button>
      </div>
    </div>
  );
}

export default App;
