import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import { getContractWithSigner, getContractWithProvider } from "./contractUtils";

function App() {
  const [walletAddress, setWalletAddress] = useState("");
  const [recipientAddress, setRecipientAddress] = useState("");
  const [rewardAmount, setRewardAmount] = useState("");
  const [totalRewards, setTotalRewards] = useState(null);
  const [contractWithSigner, setContractWithSigner] = useState(null);

  // Connect Wallet Function
  const connectWallet = async () => {
    if (window.ethereum) {
      try {
        const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
        setWalletAddress(accounts[0]);
        await loadContractWithSigner();
        await fetchTotalRewards();
      } catch (error) {
        console.error("Wallet connection failed:", error);
      }
    } else {
      alert("MetaMask not detected");
    }
  };

  // Auto wallet refresh listener
  useEffect(() => {
    if (window.ethereum) {
      window.ethereum.on("accountsChanged", (accounts) => {
        setWalletAddress(accounts[0]);
        loadContractWithSigner();
        fetchTotalRewards();
      });
    }
  }, []);

  // Load contract instance with signer (for sending tx)
  const loadContractWithSigner = async () => {
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();
    const contract = getContractWithSigner(signer);
    setContractWithSigner(contract);
  };

  // Fetch total rewards from contract (read-only)
  const fetchTotalRewards = async () => {
    try {
      const contract = getContractWithProvider();
      const total = await contract.getTotalRewards();
      setTotalRewards(total.toString());
    } catch (err) {
      console.error("Error fetching total rewards:", err);
    }
  };

  // Reward user function (write)
  const handleRewardUser = async () => {
    if (!contractWithSigner) return;
    if (!ethers.utils.isAddress(recipientAddress)) {
      alert("Invalid recipient address");
      return;
    }
    if (!rewardAmount || isNaN(rewardAmount) || Number(rewardAmount) <= 0) {
      alert("Invalid reward amount");
      return;
    }

    try {
      const tx = await contractWithSigner.rewardUser(recipientAddress, ethers.BigNumber.from(rewardAmount));
      await tx.wait();
      alert("Reward transaction confirmed!");
      fetchTotalRewards();
      setRecipientAddress("");
      setRewardAmount("");
    } catch (err) {
      console.error("Error rewarding user:", err);
      alert("Transaction failed. See console.");
    }
  };

  return (
    <div style={{ padding: "30px" }}>
      <h1>üéÅ Blockchain Reward System</h1>

      {!walletAddress && <button onClick={connectWallet}>üîå Connect Wallet</button>}

      {walletAddress && (
        <>
          <p>
            ‚úÖ Connected: <b>{walletAddress}</b>
          </p>

          <div>
            <input
              type="text"
              placeholder="Recipient Address"
              value={recipientAddress}
              onChange={(e) => setRecipientAddress(e.target.value)}
              style={{ width: "300px", marginRight: "10px" }}
            />
            <input
              type="number"
              placeholder="Reward Amount"
              value={rewardAmount}
              onChange={(e) => setRewardAmount(e.target.value)}
              style={{ width: "120px", marginRight: "10px" }}
            />
            <button onClick={handleRewardUser}>Reward User</button>
          </div>

          <h3>Total Rewards: {totalRewards !== null ? totalRewards : "Loading..."}</h3>
        </>
      )}
    </div>
  );
}

export default App;
